document.addEventListener('DOMContentLoaded', function(){

  if ($('preview-container')) {
    const $previewContainer = $('#preview-container');

    const createImageHTML  = (blob) => {

      // const imageElement = document.createElement('div');
      // imageElement.setAttribute('id', 'default');

      // const blobElement = document.createElement('img');
      // blobElement.setAttribute('src', blob);

      const $imageElement = $('<div>').attr('id', 'default');
      const $blobElement = $('<img>').attr('src', blob);

      // if (document.URL.match('articles')) {
      //   blobElement.setAttribute('id', 'preview-jacket')
      // } else {
      //   blobElement.setAttribute('id', 'preview-icon');
      // }

      if ($(location).match('article')) {
        $blobElement.attr('id', 'preview-jacket');
      } else {
        $blobElement.attr('id', 'preview-icon');
      }

      // const defaultImage = document.getElementById('default');
      // defaultImage.remove()

      $('#default').remove();

      // imageElement.appendChild(blobElement);
      // iconBox.appendChild(imageElement);

      $imageElement.append($blobElement);
      $previewContainer.append($imageElement);
    };

    // document.getElementById('form').addEventListener('change', function(e){
    //   const alreadyImage = document.querySelector('default')
    //   if (alreadyImage) {
    //     alreadyImage.remove();
    //   };

    //   const file = e.target.files[0];
    //   const blob = window.URL.createObjectURL(file);

    //   createImageHTML(blob);
    // });

    $('#form').on('change', function(image) {
      if ($('#default')) {
        $('#default').remove();
      }

      const file = e.target.files[0];
      const blob = window.URL.createObjectURL(file);

      createImageHTML(blob);
    })
  };
});
