document.addEventListener('DOMContentLoaded', function(){

  if (document.getElementById('preview-container')) {
    const iconBox = document.getElementById('preview-container');

    const createImageHTML  = (blob) => {

      const imageElement = document.createElement('div');
      imageElement.setAttribute('class', 'image-box');
      imageElement.setAttribute('id', 'default');

      const blobElement = document.createElement('img');
      blobElement.setAttribute('src', blob);
      blobElement.setAttribute('id', 'preview-image');

      const defaultImage = document.getElementById('default');
      defaultImage.remove()

      imageElement.appendChild(blobElement);
      iconBox.appendChild(imageElement);
    };

    document.getElementById('form').addEventListener('change', function(e){
      const alreadyImage = document.querySelector('default')
      if (alreadyImage) {
        alreadyImage.remove();
      };

      const file = e.target.files[0];
      const blob = window.URL.createObjectURL(file);

      createImageHTML(blob);
    });

  };
});
